import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [name, setName] = useState("");
  const [wish, setWish] = useState("");

  const sendWish = async () => {
    if (!name) return alert("Please enter a name");

    try {
      const res = await axios.post("http://localhost:5000/api/wish", { name });
      setWish(res.data.message);
    } catch (err) {
      console.error(err);
      alert("Backend not running or error occurred.");
    }
  };

  return (
    <div style={{ padding: 40, fontFamily: "Arial" }}>
      <h1>ðŸŽ‚ SKR SURPRISE BOT ðŸŽ‰</h1>
      <h3>ðŸŽ‚ Please enter your name your  ðŸŽ‰</h3>
      <input
        placeholder="Enter name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ padding: 6 }}
      />
      <button onClick={sendWish} style={{ marginLeft: 10 }}>
        Wish
      </button>

      {wish && (
        <div style={{ marginTop: 20 }}>
          <h2>Wish:</h2>
          <p>{wish}</p>
        </div>
      )}
    </div>
  );
}
