import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [name, setName] = useState("");
  const [wish, setWish] = useState("");

  const sendWish = async () => {
    if (!name) return alert("Please enter a name");

    try {
      const res = await axios.post("http://localhost:3000/api/wish", { name });
      setWish(res.data.message);
    } catch (err) {
      console.error(err);
      alert("Backend not running or error occurred.");
    }
  };

  const [boxOpen, setBoxOpen] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleOpen = async () => {
    if (!name) return alert("Please enter a name");
    setLoading(true);
    try {
      await sendWish(); // sendWish sets `wish`
      // slight delay so the UI animates after wish is set
      setTimeout(() => {
        setBoxOpen(true);
        setLoading(false);
      }, 250);
    } catch (e) {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 40, fontFamily: "Arial" }}>
      <h1>ðŸŽ‰ SKR SURPRISE BOT ðŸŽ‰</h1>
      <h3>ðŸŽ‰ Please enter your name ðŸŽ‰</h3>

      <div style={{ marginBottom: 12 }}>
        <input
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ padding: 6 }}
        />
        <button
          onClick={handleOpen}
          style={{ marginLeft: 10, padding: "6px 12px" }}
          disabled={loading}
        >
          {loading ? "Loading..." : "Open Surprise Box"}
        </button>
        <button
          onClick={() => {
            setBoxOpen(false);
            setWish("");
          }}
          style={{ marginLeft: 8, padding: "6px 12px" }}
        >
          Reset
        </button>
      </div>

      <div style={{ marginTop: 20, display: "flex", alignItems: "flex-start", gap: 24 }}>
        {/* Surprise box */}
        <div style={{ width: 240, height: 160, position: "relative", perspective: 800 }}>
          {/* box body */}
          <div
            style={{
              position: "absolute",
              left: 0,
              right: 0,
              bottom: 0,
              height: "70%",
              background: "#F5A623",
              borderRadius: 8,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "inset 0 6px 12px rgba(0,0,0,0.12)",
              transform: boxOpen ? "translateY(8px)" : "translateY(0)",
              transition: "transform 500ms",
              padding: 12,
              textAlign: "center",
            }}
          >
            {wish ? (
              <div style={{ fontSize: 16, fontWeight: 600 }}>{wish}</div>
            ) : (
              <div style={{ color: "#6b6b6b" }}>Your wish will appear here</div>
            )}
          </div>

          {/* lid */}
          <div
            style={{
              position: "absolute",
              left: 0,
              right: 0,
              top: 0,
              height: "45%",
              background: "#D64545",
              borderRadius: 8,
              transformOrigin: "top center",
              transform: boxOpen ? "rotateX(-120deg) translateY(-10px)" : "rotateX(0deg)",
              transition: "transform 600ms cubic-bezier(.2,.8,.2,1)",
              boxShadow: "0 8px 18px rgba(0,0,0,0.25)",
            }}
          />

          {/* little ribbon */}
          <div
            style={{
              position: "absolute",
              top: 12,
              left: "50%",
              transform: "translateX(-50%)",
              width: 8,
              height: 48,
              background: "#fff",
              borderRadius: 4,
              opacity: 0.9,
            }}
          />
        </div>

        {/* reveal area */}
        <div style={{ maxWidth: 360 }}>
          {boxOpen && wish ? (
            <>
              <h3>Surprise!</h3>
              <p style={{ marginTop: 6 }}>{wish}</p>
            </>
          ) : (
            <>
              <h3>How it works</h3>
              <p style={{ marginTop: 6, color: "#666" }}>
                Enter your name, click "Open Surprise Box" and watch your wish appear inside the box.
              </p>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
